
public class Qst1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int firstMoney = 14000;
	firstMoney += firstMoney * 0.4;
	System.out.println("첫 해 투자자금은 "+ firstMoney);
	
	firstMoney-=1500;
	System.out.println("두번째 해 투자자금은 " + firstMoney);
	
	firstMoney += firstMoney * 0.12;
	System.out.println("세번째 해 투자자금은 "+ firstMoney);

	}
}
