public class Qst2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int first_number = 205;
		int second_number = 20;
		int numOne = first_number / second_number;
		int numTwo = first_number % second_number;
		System.out.println("두 값을 나눈 값은 " + numOne + "\t두 값 나누기의 나머지는 " + numTwo);
	
	}
}
